    for (int i = 0; i < self.timeArray.count ; i ++) {
        LSDModel *model = self.timeArray[i];
        [model timeDown];
        
//        [self.timeArray replaceObjectAtIndex:i withObject:model];
    }

//    [self.tableView reloadData];
    [[NSNotificationCenter defaultCenter] postNotificationName:@"LSDTimeNotification" object:nil];
   
}


-(void)setupUI
{

    UITableView *tableView = [[UITableView alloc]initWithFrame:self.view.frame style:UITableViewStylePlain];
    
    tableView.delegate = self;
    tableView.dataSource = self;
    
    [self.view addSubview:tableView];
    self.tableView = tableView;
}

-(NSInteger)tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section
{

    return self.timeArray.count;
    
}

-(UITableViewCell *)tableView:(UITableView *)tableView cellForRowAtIndexPath:(NSIndexPath *)indexPath
{

   LSDTableViewCell *cell = [LSDTableViewCell tableViewCellWithTable:tableView];
    
   LSDModel *model = self.timeArray[indexPath.row];

   [cell loadModel:model indexPath:indexPath];
    
    return cell;
    
}

-(void)tableView:(UITableView *)tableView willDisplayCell:(UITableViewCell *)cell forRowAtIndexPath:(NSIndexPath *)indexPath
{
    LSDTableViewCell *timeCell = (LSDTableViewCell *)cell;
    
    timeCell.isDisplayed = YES;
    
    [timeCell loadModel:self.timeArray[indexPath.row] indexPath:indexPath];

}

-(void)tableView:(UITableView *)tableView didEndDisplayingCell:(UITableViewCell *)cell forRowAtIndexPath:(NSIndexPath *)indexPath
{

    LSDTableViewCell *timeCell = (LSDTableViewCell *)cell;
    timeCell.isDisplayed = NO;
    
}

-(CGFloat)tableView:(UITableView *)tableView heightForRowAtIndexPath:(NSIndexPath *)indexPath
{

    return 100;
}

@end
